                        },
                        {
                            "id": "4",
                            "title": "Piccadilly Circus, London"
                        }
                    ]
                },
                "is_location_enabled": {
                    "type": "boolean",
                    "__example__": true
                },
                "date": {
                    "type": "array",
                    "items": {
                        "type": "object",
                        "properties": {
                            "id": {
                                "type": "string"
                            },
                            "title": {
                                "type": "string"
                            }
                        }
                    },
                    "__example__": [
                        {
                            "id": "2024-01-01",
                            "title": "Mon Jan 01 2024"
                        },
                        {
                            "id": "2024-01-02",
                            "title": "Tue Jan 02 2024"
                        },
                        {
                            "id": "2024-01-03",
                            "title": "Wed Jan 03 2024"
                        }
                    ]
                },
                "is_date_enabled": {
                    "type": "boolean",
                    "__example__": true
                },
                "time": {
                    "type": "array",
                    "items": {
                        "type": "object",
                        "properties": {
                            "id": {
                                "type": "string"
                            },
                            "title": {
                                "type": "string"
                            }
                        }
                    },
                    "__example__": [
                        {
                            "id": "10:30",
                            "title": "10:30"
                        },
                        {
                            "id": "11:00",
                            "title": "11:00",
                            "enabled": false
                        },
                        {
                            "id": "11:30",
                            "title": "11:30"
                        },
                        {
                            "id": "12:00",
                            "title": "12:00",
                            "enabled": false
                        },
                        {
                            "id": "12:30",
                            "title": "12:30"
                        }
                    ]
                },
                "is_time_enabled": {
                    "type": "boolean",
                    "__example__": true
                }
            },
            "layout": {
                "type": "SingleColumnLayout",
                "children": [
                    {
                        "type": "Form",
                        "name": "appointment_form",
                        "children": [
                            {
                                "type": "Dropdown",
                                "label": "Department",
                                "name": "department",
                                "data-source": "${data.department}",
                                "required": true,
                                "on-select-action": {
                                    "name": "data_exchange",
                                    "payload": {
                                        "trigger": "department_selected",
                                        "department": "${form.department}"
                                    }
                                }
                            },
                            {
                                "type": "Dropdown",
                                "label": "Location",
                                "name": "location",
                                "data-source": "${data.location}",
                                "required": "${data.is_location_enabled}",
                                "enabled": "${data.is_location_enabled}",
                                "on-select-action": {
                                    "name": "data_exchange",
                                    "payload": {
                                        "trigger": "location_selected",
                                        "department": "${form.department}",
                                        "location": "${form.location}"
                                    }
                                }
                            },
                            {
                                "type": "Dropdown",
                                "label": "Date",
                                "name": "date",
                                "data-source": "${data.date}",
                                "required": "${data.is_date_enabled}",
                                "enabled": "${data.is_date_enabled}",
                                "on-select-action": {
                                    "name": "data_exchange",
                                    "payload": {
                                        "trigger": "date_selected",
                                        "department": "${form.department}",
                                        "location": "${form.location}",
                                        "date": "${form.date}"
                                    }
                                }
                            },
                            {
                                "type": "Dropdown",
                                "label": "Time",
                                "name": "time",
                                "data-source": "${data.time}",
                                "required": "${data.is_time_enabled}",
                                "enabled": "${data.is_time_enabled}"
                            },
                            {
                                "type": "Footer",
                                "label": "Continue",
                                "on-click-action": {
                                    "name": "navigate",
                                    "next": {
                                        "type": "screen",
                                        "name": "DETAILS"
                                    },
                                    "payload": {
                                        "department": "${form.department}",
                                        "location": "${form.location}",
                                        "date": "${form.date}",
                                        "time": "${form.time}"
                                    }
                                }
                            }
                        ]
                    }
                ]
            }
        },
        {
